//principal -> usuario 

import react from "react"

//sugestoes
export default function Sidebar() {
    const sugest = [
        { image: "assets/img/chibirdart.svg", user: "chibirdart", reason: "Segue você" },
        { image: "assets/img/razoesparaacreditar.svg", user: "razoesparaacreditar", reason: "Novo no Instagram" },
        { image: "assets/img/adorable_animals.svg", user: "adorable_animals", reason: "Segue você" },
        { image: "assets/img/smallcutecats.svg", user: "smallcutecats", reason: "Segue você" }
    ]
    const mainUser = [
        { mainImage: "assets/img/catanacomics.svg", main: "Catana" }
    ]
    return (
        <div class="sidebar">
            {mainUser.map((a) => <UsuarioLogado mainImage={a.mainImage} mainName={a.mainName} />)}

            <div class="sugestoes">
                <div class="titulo">
                    Sugestões para você
                    <div>Ver tudo</div>
                </div>
                {sugest.map((a) => <Sugestoes image={a.image} user={a.user} reason={a.reason} />)}

            </div>

            <div class="links">
                Sobre • Ajuda • Imprensa • API • Carreiras • Privacidade • Termos • Localizações • Contas mais relevantes •
                Hashtags • Idioma
            </div>

            <div class="copyright">
                © 2021 INSTAGRAM DO FACEBOOK
            </div>
        </div>
    )
}

function Sugestoes(props) {
    console.log(props)
    return (
        <div class="sugestao">
            <div class="usuario">
                <img src={props.image} />
                <div class="texto">
                    <div class="nome">{props.user}</div>
                    <div class="razao">{props.reason}</div>
                </div>
            </div>

            <div class="seguir">Seguir</div>
        </div>
    )
}
function UsuarioLogado(props) {
    const [main, setMain] = React.useState('Cortana')
    return (
        <div class="usuario">
            <img src={props.mainImage} />
            <div class="texto">
                <strong>catanacomics</strong>
                <span>
                    {props.main}
                    <ion-icon name="pencil" onClick={(() => setMain(Pergunta(main)))}></ion-icon>
                </span>
            </div>
        </div>
    )
}
function Pergunta(main){
    main = prompt("qual seu nome porra?")
}
