import React from "react"
//posts: img / usuario


export default function Posts() {
    const infoPost = [
        { userImage: "assets/img/meowed.svg", userName: "meowed", content: "assets/img/gato-telefone.svg", lastLikeImage: "assets/img/respondeai.svg", lastLikeUser: "respondeai", like: "outras 101.523 pessoas" },
        { userImage: "assets/img/barked.svg", userName: "barked", content: "assets/img/dog.svg", lastLikeImage: "assets/img/adorable_animals.svg", lastLikeUser: "dorable_animals", like: "outras 99.159 pessoas" }
    ]
    return (
        <div class="posts">
            {infoPost.map((a) => <Post userImage={a.userImage} userName={a.userName} content={a.content} lastLikeImage={a.lastLikeImage} lastLikeUser={a.lastLikeUser} like={a.like} />)}
        </div>

    )
}

let like = ""
//condição onde ser curtida for true adiciona a classe 
function Post(props) {
    console.log(props)

    const [isLike, setLike] = React.useState(false)
    const [isSave, setSave] = React.useState(false)

    function numLike(){
        const [number, setNumber] = React.useState(props.like)
        if(isLive === true){
            setNumber(props.like + 1)
        }
        else{
            setNumber(props.like -1)
        }
    }

    return (
        <div class="post">
            <div class="topo">
                <div class="usuario">
                    <img src={props.userImage} />
                    {props.userName}
                </div>
                <div class="acoes">
                    <ion-icon name="ellipsis-horizontal"></ion-icon>
                </div>
            </div>

            <div class="conteudo">
                <img src={props.content} onClick={(() => setLike(!isLike))} />
            </div>

            <div class="fundo">
                <div class="acoes">
                    <div >  

                        {isLike ? <ion-icon name="heart" onClick={(() => setLike(!isLike))} class="like"></ion-icon> : <ion-icon name="heart-outline" onClick={(() => setLike(!isLike))}></ion-icon>}
                        <ion-icon name="chatbubble-outline"></ion-icon>
                        <ion-icon name="paper-plane-outline"></ion-icon>
                    </div>
                    <div>
                        {isSave ? <ion-icon name="bookmark" class="save" onClick={(() => setSave(!isSave))}></ion-icon> : <ion-icon name="bookmark-outline" onClick={(() => setSave(!isSave))}></ion-icon> }
                    </div>
                </div>

                <div class="curtidas">
                    <img src={props.lastLikeImage}  />
                    <div class="texto">
                        Curtido por <strong>{props.lastLikeUser}</strong> e <strong>{number}</strong>
                    </div>
                </div>
            </div>
        </div>
    )
}
