//posts: img / usuario

export default function Posts() {
    const post = [
        {image:"assets/img/meowed.svg", user:"meowed"},
        {image:"assets/img/barked.svg", user:"barked"}
    ]
    return (
        <div class="posts">
            <div class="post">
                {top.map((a) => <Topo image={a.image} user={a.user} />)}
                <Conteudo image="assets/img/gato-telefone.svg" />

                <Curtida image="assets/img/respondeai.svg" user="respondeai" like="outras 101.523 pessoas"/>
            </div>


            <div class="post">
                <Topo image="assets/img/barked.svg" user="barked" />
                <Conteudo image="assets/img/dog.svg" />

                <Curtida image="assets/img/adorable_animals.svg" user="adorable_animals" like="outras 99.159 pessoas"/>
            </div>
        </div>
    )
}

function Topo(props) {
    return (
        <div class="topo">
            <div class="usuario">
                <img src={props.image} />
                {props.user}
            </div>
            <div class="acoes">
                <ion-icon name="ellipsis-horizontal"></ion-icon>
            </div>
        </div>
    )
}

function Conteudo(props) {
    return (
        <div class="conteudo">
            <img src={props.image} />
        </div>
    )
}
function Curtida(props) {
    return (

        <div class="fundo">
            <div class="acoes">
                <div>
                    <ion-icon name="heart-outline"></ion-icon>
                    <ion-icon name="chatbubble-outline"></ion-icon>
                    <ion-icon name="paper-plane-outline"></ion-icon>
                </div>
                <div>
                    <ion-icon name="bookmark-outline"></ion-icon>
                </div>
            </div>

            <div class="curtidas">
                <img src={props.image} />
                <div class="texto">
                    Curtido por <strong>{props.user}</strong> e <strong> {props.like} </strong>
                </div>
            </div>
        </div>

    )
}
