//posts: img / usuario


function Topo(props) {
    return (
        <div class="topo">
            <div class="usuario">
                <img src={props.image} />
                {props.user}
            </div>
            <div class="acoes">
                <ion-icon name="ellipsis-horizontal"></ion-icon>
            </div>
        </div>
    )
}

function Conteudo(props) {


    return (
        <div class="conteudo">
            <img src={props.image} />
        </div>
    )
}

function Icon() {
    let icon = document.querySelector('.acoes > .like')
    console.log(icon)
    return (
        <div class="fundo">
        <div class="acoes">
          <div>
          <ion-icon name="heart"></ion-icon>
            <ion-icon name="chatbubble-outline"></ion-icon>
            <ion-icon name="paper-plane-outline"></ion-icon>
          </div>
          <div>
            <ion-icon name="bookmark-outline"></ion-icon>
          </div>
        </div>

        <div class="curtidas">
          <img src="assets/img/respondeai.svg" />
          <div class="texto">
            Curtido por <strong>respondeai</strong> e <strong>outras 101.523 pessoas</strong>
          </div>
        </div>
      </div>
    )
}

export default function Posts() {
    return (
        <div class="posts">
            <div class="post">
                <Topo image="assets/img/meowed.svg" user="meowed" />
                <Conteudo image="assets/img/gato-telefone.svg" />

                    <Icon />
                </div>


            <div class="post">
                <Topo image="assets/img/barked.svg" user="barked" />
                <Conteudo image="assets/img/dog.svg" />


                    <Icon />

            </div>
        </div>
    )
}
